#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

const int MAXN = 800 + 1;
const int MAXA = 12000 + 1;
const int MAXN2 = 7 + 1;

int gcd(int a, int b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

void dfs(int p, int q, int a, int n, int cur, int prod, vector<int>& path, int& res) {
    if (path.size() > n) return;
    if (prod > a) return;
    if (p == 0) {
        res++;
        return;
    }
    for (int i = 1; i <= q; i++) {
        if (gcd(p, i) == 1) {
            path.push_back(i);
            dfs(p / i, q, a, n, cur + 1, prod * i, path, res);
            path.pop_back();
        }
    }
}

int main() {
    int p, q, a, n;
    while (cin >> p >> q >> a >> n) {
        if (p == 0 && q == 0 && a == 0 && n == 0) break;
        int res = 0;
        vector<int> path;
        dfs(p, q, a, n, 0, 1, path, res);
        cout << res << endl;
    }
    return 0;
}
