#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

struct Point {
    double x, y;
};

bool comparePoints(const Point& a, const Point& b) {
    return (a.x < b.x) || (a.x == b.x && a.y < b.y);
}

int distance(const Point& a, const Point& b) {
    return (int)round(sqrt(pow(a.x - b.x, 2) + pow(a.y - b.y, 2)));
}

int maxPointsEnclosed(const vector<Point>& points) {
    sort(points.begin(), points.end(), comparePoints);
    int maxPoints = 1;
    int currentPoints = 1;
    int prevDistance = distance(points[0], points[1]);
    for (int i = 2; i < points.size(); i++) {
        int dist = distance(points[i], points[i - 1]);
        if (dist <= prevDistance + 1) {
            currentPoints++;
        } else {
            maxPoints = max(maxPoints, currentPoints);
            currentPoints = 1;
        }
        prevDistance = dist;
    }
    return max(maxPoints, currentPoints);
}

int main() {
    int n;
    while (cin >> n && n != 0) {
        vector<Point> points(n);
        for (int i = 0; i < n; i++) {
            cin >> points[i].x >> points[i].y;
        }
        cout << maxPointsEnclosed(points) << endl;
    }
    return 0;
}
