import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def max_points(points):
    n = len(points)
    max_points = 0
    for i in range(n):
        min_dist = float('inf')
        for j in range(n):
            if i != j:
                dist = distance(points[i], points[j])
                if dist < min_dist:
                    min_dist = dist
        if min_dist > 1.0001:
            max_points = max(max_points, 1)
        else:
            max_points = max(max_points, 0)
    return max_points + 1

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        points = []
        for _ in range(n):
            x, y = map(float, input().split())
            points.append((x, y))
        print(max_points(points))

if __name__ == "__main__":
    main()
