import java.util.*;
import java.io.*;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int D = Integer.parseInt(br.readLine());
        for (int d = 0; d < D; d++) {
            int N = Integer.parseInt(br.readLine());
            int[][] boards = new int[N][2];
            for (int i = 0; i < N; i++) {
                StringTokenizer st = new StringTokenizer(br.readLine());
                boards[i][0] = Integer.parseInt(st.nextToken());
                boards[i][1] = Integer.parseInt(st.nextToken());
            }
            int M = Integer.parseInt(br.readLine());
            int[][] faucets = new int[M][2];
            for (int i = 0; i < M; i++) {
                StringTokenizer st = new StringTokenizer(br.readLine());
                faucets[i][0] = Integer.parseInt(st.nextToken());
                faucets[i][1] = Integer.parseInt(st.nextToken());
            }
            int L = Integer.parseInt(br.readLine());
            int[] observations = new int[L];
            int[] times = new int[L];
            for (int i = 0; i < L; i++) {
                StringTokenizer st = new StringTokenizer(br.readLine());
                observations[i] = Integer.parseInt(st.nextToken());
                times[i] = Integer.parseInt(st.nextToken());
            }
            Arrays.sort(boards, Comparator.comparingInt(a -> a[0]));
            Arrays.sort(faucets, Comparator.comparingInt(a -> a[0]));
            double[] heights = new double[L];
            double[] rates = new double[M];
            for (int i = 0; i < M; i++) {
                rates[i] = faucets[i][1] / (faucets[i][0] - boards[0][0]);
            }
            for (int i = 0; i < L; i++) {
                double height = 0;
                for (int j = 0; j < N; j++) {
                    double rate = 0;
                    for (int k = 0; k < M; k++) {
                        if (faucets[k][0] > boards[j][0]) {
                            rate += rates[k];
                        }
                    }
                    height += (boards[j][1] - height) * (times[i] - (boards[j][0] - observations[i]) / 30);
                }
                heights[i] = height;
            }
            for (int i = 0; i < L; i++) {
                System.out.printf("%.4f%n", Math.min(heights[i], 50));
            }
        }
    }
}
