from collections import defaultdict

def solve():
    N = int(input())
    graph = defaultdict(list)
    for _ in range(N):
        u, v = input().split('-')
        graph[u].append(v)
        graph[v].append(u)

    def dfs(u, v, visited):
        if u == v:
            return True
        visited.add(u)
        for neighbor in graph[u]:
            if neighbor not in visited:
                if dfs(neighbor, v, visited):
                    return True
        return False

    def are_orthogonal(u, v):
        return dfs(u, v, set())

    def are_stronger(u, v):
        return are_orthogonal(u, v) and u > v

    M = int(input())
    for _ in range(M):
        u, v = input().split('-')
        if are_stronger(u, v):
            print('YES')
        else:
            print('NO')

while True:
    try:
        solve()
    except ValueError:
        break
    try:
        input()
    except NameError:
        break
