#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAX 1000000000

typedef struct {
    char type;
    double rate;
    int charge;
} Operation;

double calculate(double balance, int years, char type, double rate, int charge) {
    double interest = 0;
    for (int i = 0; i < years; i++) {
        interest = (balance * rate) + interest;
        if (type == '0') {
            interest = (int) interest;
        }
        balance = (balance + interest - charge);
        if (balance < 0) {
            balance = 0;
        }
    }
    return balance + interest;
}

int main() {
    int m;
    scanf("%d", &m);
    for (int i = 0; i < m; i++) {
        int initial, years, n;
        scanf("%d %d %d", &initial, &years, &n);
        Operation *operations = (Operation *) malloc(n * sizeof(Operation));
        for (int j = 0; j < n; j++) {
            scanf(" %c %lf %d", &operations[j].type, &operations[j].rate, &operations[j].charge);
        }
        double max = 0;
        for (int j = 0; j < n; j++) {
            double balance = calculate(initial, years, operations[j].type, operations[j].rate, operations[j].charge);
            if (balance > max) {
                max = balance;
            }
        }
        printf("%d\n", (int) max);
        free(operations);
    }
    return 0;
}
