import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int m = scanner.nextInt();
        for (int i = 0; i < m; i++) {
            long A = scanner.nextLong();
            int n = scanner.nextInt();
            int k = scanner.nextInt();
            long[] max = new long[k];
            for (int j = 0; j < k; j++) {
                String[] operation = scanner.next().split(" ");
                double r = Double.parseDouble(operation[1]);
                int c = Integer.parseInt(operation[2]);
                long[] balance = new long[n + 1];
                balance[0] = A;
                for (int l = 1; l <= n; l++) {
                    if (operation[0].equals("0")) {
                        balance[l] = (long) (balance[l - 1] * r + 0.5) - c;
                    } else {
                        balance[l] = (long) (balance[l - 1] * r + 0.5);
                    }
                }
                max[j] = balance[n];
            }
            long maxVal = 0;
            for (long l : max) {
                if (l > maxVal) {
                    maxVal = l;
                }
            }
            System.out.println(maxVal);
        }
    }
}
