#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAX_N 50
#define MAX_M 10

typedef struct {
    int x[MAX_M];
    int y[MAX_M];
    int n;
} Polygon;

typedef struct {
    Polygon poly[MAX_N];
    int n;
} Dataset;

int rotate(Polygon *p, int k) {
    int i;
    for (i = 0; i < p->n; i++) {
        int temp = p->x[i];
        p->x[i] = p->y[i];
        p->y[i] = temp;
    }
    for (i = 0; i < p->n; i++) {
        p->x[i] = -p->x[i];
        p->y[i] = -p->y[i];
    }
    for (i = 0; i < p->n; i++) {
        p->x[i] = p->x[i] * cos(k * M_PI / 2) - p->y[i] * sin(k * M_PI / 2);
        p->y[i] = p->x[i] * sin(k * M_PI / 2) + p->y[i] * cos(k * M_PI / 2);
    }
}

int match(Polygon *p1, Polygon *p2) {
    int i, j;
    for (i = 0; i < p1->n; i++) {
        for (j = 0; j < p2->n; j++) {
            if (p1->x[i] == p2->x[j] && p1->y[i] == p2->y[j]) {
                break;
            }
        }
        if (j == p2->n) {
            return 0;
        }
    }
    for (i = 0; i < p2->n; i++) {
        for (j = 0; j < p1->n; j++) {
            if (p2->x[i] == p1->x[j] && p2->y[i] == p1->y[j]) {
                break;
            }
        }
        if (j == p1->n) {
            return 0;
        }
    }
    return 1;
}

int main() {
    Dataset d;
    d.n = 0;
    Polygon p;
    p.n = 0;
    int i, j, k, l;
    char str[100];
    while (scanf("%d", &p.n) == 1) {
