            scanf("%d %d", &p.x[i], &p.y[i]);
        }
        for (i = 0; i < d.n; i++) {
            for (j = 0; j < 4; j++) {
                rotate(&d.poly[i], j);
                if (match(&p, &d.poly[i])) {
                    d.poly[i].n = 0;
                    break;
                }
            }
            if (d.poly[i].n != 0) {
                d.poly[i].n = 0;
