import java.util.*;
import java.io.*;

public class Main {
    static class Polygon {
        int[] x, y;
        int n;

        Polygon(int[] x, int[] y, int n) {
            this.x = x;
            this.y = y;
            this.n = n;
        }

        Polygon rotate() {
            int[] nx = new int[n];
            int[] ny = new int[n];
            for (int i = 0; i < n; i++) {
                nx[i] = y[i];
                ny[i] = -x[i];
            }
            return new Polygon(nx, ny, n);
        }

        Polygon translate(int dx, int dy) {
            int[] nx = new int[n];
            int[] ny = new int[n];
            for (int i = 0; i < n; i++) {
                nx[i] = x[i] + dx;
                ny[i] = y[i] + dy;
            }
            return new Polygon(nx, ny, n);
        }

        boolean match(Polygon other) {
            if (n != other.n) return false;
            for (int i = 0; i < n; i++) {
                if (x[i] != other.x[i] && y[i] != other.y[i]) return false;
            }
            return true;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line;
        while ((line = br.readLine()) != null) {
            if (line.equals("0")) break;
            int n = Integer.parseInt(line);
            Polygon template = null;
            List<Polygon> polys = new ArrayList<>();
            for (int i = 0; i < n; i++) {
                line = br.readLine();
                int m = Integer.parseInt(line);
                int[] x = new int[m];
                int[] y = new int[m];
                for (int j = 0; j < m; j++) {
                    String[] parts = br.readLine().split(" ");
                    x[j] = Integer.parseInt(parts[0]);
                    y[j] = Integer.parseInt(parts[1]);
                }
                polys.add(new Polygon(x, y, m));
            }
            polys.add(template = polys.get(0));
            for (int i = 1; i < n; i++) {
                polys.get(i).rotate();
                polys.get(i).translate(-template.x[0], -template.y[0]);
            }
            Set<Integer> matches = new HashSet<>();
            for (int i = 1; i < n; i++) {
                if (template.match(polys.get(i))) matches.add(i + 1);
            }
            for (int match : matches) System.out.println(match);
            System.out.println("+++++");
        }
    }
}
