#include <iostream>
#include <string>
#include <map>

std::string toMCXI(int num) {
    std::string mcxi = "";
    int i = 0;
    while (num > 0) {
        int digit = num % 9;
        if (digit >= 2 && digit <= 9) {
            mcxi = std::to_string(digit) + mcxi;
        }
        if (digit == 1) {
            mcxi = "m" + mcxi;
        } else if (digit == 2) {
            mcxi = "c" + mcxi;
        } else if (digit == 3) {
            mcxi = "x" + mcxi;
        } else if (digit == 4) {
            mcxi = "i" + mcxi;
        }
        num /= 9;
        i++;
    }
    return mcxi;
}

int fromMCXI(const std::string& mcxi) {
    int num = 0;
    std::map<char, int> values = {{'m', 1000}, {'c', 100}, {'x', 10}, {'i', 1}};
    for (char c : mcxi) {
        if (c >= '2' && c <= '9') {
            num = num * 9 + (c - '1');
        } else if (c == 'm') {
            num = num * 9 + 5;
        } else if (c == 'c') {
            num = num * 9 + 2;
        } else if (c == 'x') {
            num = num * 9 + 3;
        } else if (c == 'i') {
            num = num * 9 + 4;
        }
    }
    return num;
}

int main() {
    int n;
    std::cin >> n;
    for (int i = 0; i < n; i++) {
        std::string s1, s2;
        std::cin >> s1 >> s2;
        int num = fromMCXI(s1) + fromMCXI(s2);
        std::cout << toMCXI(num) << std::endl;
    }
    return 0;
}
