import java.util.HashMap;
import java.util.Map;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        scanner.nextLine();

        for (int i = 0; i < n; i++) {
            String[] strings = scanner.nextLine().split(" ");
            System.out.println(toMCXIString(sumMCXIValues(strings[0], strings[1])));
        }
    }

    private static int sumMCXIValues(String s1, String s2) {
        Map<Character, Integer> map = new HashMap<>();
        map.put('m', 1000);
        map.put('c', 100);
        map.put('x', 10);
        map.put('i', 1);

        int sum = 0;
        int i = 0;
        while (i < s1.length()) {
            if (Character.isDigit(s1.charAt(i))) {
                int j = i + 1;
                while (j < s1.length() && Character.isDigit(s1.charAt(j))) {
                    j++;
                }
                sum += Integer.parseInt(s1.substring(i, j)) * map.get(s1.charAt(j));
                i = j;
            } else {
                sum += map.get(s1.charAt(i));
                i++;
            }
        }

        i = 0;
        while (i < s2.length()) {
            if (Character.isDigit(s2.charAt(i))) {
                int j = i + 1;
                while (j < s2.length() && Character.isDigit(s2.charAt(j))) {
                    j++;
                }
                sum += Integer.parseInt(s2.substring(i, j)) * map.get(s2.charAt(j));
                i = j;
            } else {
                sum += map.get(s2.charAt(i));
                i++;
            }
        }

        return sum;
    }

    private static String toMCXIString(int n) {
        StringBuilder sb = new StringBuilder();
        int[] values = {1000, 100, 10, 1};
        int[] digits = {9, 8, 7, 6, 5, 4, 3, 2};

        for (int value : values) {
            if (n >= value) {
                int count = 0;
                while (n >= value) {
                    n -= value;
                    count++;
                }
                if (count > 1) {
                    sb.append(count / 2);
                    sb.append('m');
                    count %= 2;
                }
                if (count > 0) {
                    sb.append('m');
                }
            }
        }

        for (int digit : digits) {
            if (n >= digit) {
                sb.append(digit);
                n -= digit;
            }
        }

        for (int value : values) {
            if (n >= value) {
                sb.append('i');
                n -= value;
            }
        }

        return sb.toString();
    }
}
