def mcxi_value(s):
    value = 0
    i = 0
    while i < len(s):
        if s[i].isdigit():
            j = i
            while j < len(s) and s[j].isdigit():
                j += 1
            value += int(s[i:j]) * {'m': 1000, 'c': 100, 'x': 10, 'i': 1}[s[j]]
            i = j
        else:
            value += {'m': 1000, 'c': 100, 'x': 10, 'i': 1}[s[i]]
            i += 1
    return value

def mcxi_string(n):
    if n < 1000:
        return 'i' * n
    elif n < 10000:
        return 'i' + mcxi_string(n - 1000)
    elif n < 1000000:
        return 'c' + mcxi_string(n - 1000)
    elif n < 10000000:
        return 'c' + mcxi_string(n - 1000)
    else:
        return 'm' + mcxi_string(n - 1000)

def solve(s1, s2):
    value = mcxi_value(s1) + mcxi_value(s2)
    return mcxi_string(value)

n = int(input())
for _ in range(n):
    s1, s2 = input().split()
    print(solve(s1, s2))
