#include <iostream>
#include <vector>
#include <queue>
#include <limits>
#include <cmath>

using namespace std;

struct City {
    int index;
    int horses;
    vector<pair<int, int>> roads;
};

struct Road {
    int from;
    int to;
    int distance;
};

struct Route {
    int time;
    int tickets;
    int city;
};

bool operator<(const Route& a, const Route& b) {
    return a.time > b.time;
}

int main() {
    int n, m, p, a, b;
    while (cin >> n >> m >> p >> a >> b) {
        if (n == 0 && m == 0 && p == 0 && a == 0 && b == 0) break;

        vector<City> cities(m + 1);
        for (int i = 0; i < n; i++) {
            int horses;
            cin >> horses;
            cities[a].horses = horses;
        }

        for (int i = 0; i < p; i++) {
            Road road;
            cin >> road.from >> road.to >> road.distance;
            cities[road.from].roads.push_back({road.to, road.distance});
            cities[road.to].roads.push_back({road.from, road.distance});
        }

        priority_queue<Route> queue;
        queue.push({numeric_limits<int>::max(), n, a});

        vector<double> times(m + 1, numeric_limits<double>::max());
        times[a] = 0;

        while (!queue.empty()) {
            Route route = queue.top();
            queue.pop();

            if (route.city == b) {
                cout << fixed << setprecision(3) << route.time << endl;
                break;
            }

            if (route.time > times[route.city]) continue;

            for (const auto& road : cities[route.city].roads) {
                int time = route.time + road.second / cities[route.city].horses;
                if (time < times[road.first]) {
                    times[road.first] = time;
                    queue.push({time, route.tickets - 1, road.first});
                }
            }
        }

        if (times[b] == numeric_limits<double>::max()) {
            cout << "Impossible" << endl;
        }
    }

    return 0;
}
