#include <iostream>
#include <algorithm>

int main() {
    int numbers[5];

    // Read five numbers
    for (int i = 0; i < 5; i++) {
        std::cin >> numbers[i];
    }

    // Sort the numbers in descending order
    std::sort(numbers, numbers + 5, std::greater<int>());

    // Print the sorted numbers
    for (int i = 0; i < 5; i++) {
        std::cout << numbers[i] << " ";
    }

    return 0;
}
