#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define MAX_ROBOTS 100
#define MAX_ROUTE 1000
#define MAX_NAME 9

typedef struct {
    char name[MAX_NAME + 1];
    int route[MAX_ROUTE][2];
    int route_len;
} Robot;

int distance(Robot r1, Robot r2, int t) {
    int x1 = r1.route[t][0], y1 = r1.route[t][1];
    int x2 = r2.route[t][0], y2 = r2.route[t][1];
    return (int) sqrt((x2 - x1) * (x2 - x1) + (y2 - y1) * (y2 - y1));
}

int in_range(Robot r1, Robot r2, int t, int R) {
    return distance(r1, r2, t) < R + 1e-6;
}

int main() {
    int N, T, R;
    scanf("%d %d %d", &N, &T, &R);

    Robot robots[N];
    for (int i = 0; i < N; i++) {
        scanf("%s", robots[i].name);
        robots[i].route_len = 0;
        int t, vx, vy;
        scanf("%d %d %d", &t, &vx, &vy);
        robots[i].route[0][0] = vx;
        robots[i].route[0][1] = vy;
        robots[i].route[1][0] = vx;
        robots[i].route[1][1] = vy;
        robots[i].route[robots[i].route_len][0] = vx;
        robots[i].route[robots[i].route_len][1] = vy;
        robots[i].route_len++;
        for (int j = 1; j < T; j++) {
            scanf("%d %d %d", &t, &vx, &vy);
            robots[i].route[robots[i].route_len][0] = vx;
            robots[i].route[robots[i].route_len][1] = vy;
            robots[i].route_len++;
        }
    }

    for (int i = 0; i < N; i++) {
        for (int j = i + 1; j < N; j++) {
            for (int t = 0; t < T; t++) {
                if (in_range(robots[i], robots[j], t, R)) {
                    for (int k = 0; k < N; k++) {
                        if (k != i && k != j) {
                            for (int l = 0; l < T; l++) {
                                if (in_range(robots[j], robots[k], l, R) && in_range(robots[i], robots[k], l, R)) {
                                    printf("%s\n", robots[i].name);
                                    return 0;
                                }
                            }
                        }
                    }
                }
            }
        }
    }

    return 0;
}
