#include <iostream>
#include <vector>
#include <string>
#include <algorithm>
#include <cmath>
#include <set>

using namespace std;

struct Robot {
    string name;
    vector<pair<int, int>> path;
};

double distance(Robot& r1, Robot& r2, int t) {
    double x1 = r1.path[t].first;
    double y1 = r1.path[t].second;
    double x2 = r2.path[t].first;
    double y2 = r2.path[t].second;
    return sqrt((x2 - x1) * (x2 - x1) + (y2 - y1) * (y2 - y1));
}

bool inRange(Robot& r1, Robot& r2, int t, double range) {
    return distance(r1, r2, t) < range;
}

int main() {
    int N, T, R;
    cin >> N >> T >> R;

    vector<Robot> robots(N);
    for (int i = 0; i < N; i++) {
        cin >> robots[i].name;
        int t, vx, vy;
        cin >> t >> vx >> vy;
        robots[i].path.push_back({ t, { vx, vy } });
        while (true) {
            cin >> t >> vx >> vy;
            if (t == T) break;
            robots[i].path.push_back({ t, { vx, vy } });
        }
    }

    set<string> covered;
    covered.insert(robots[0].name);

    for (int t = 1; t <= T; t++) {
        for (int i = 0; i < N; i++) {
            for (int j = i + 1; j < N; j++) {
                if (inRange(robots[i], robots[j], t, R)) {
                    covered.insert(robots[j].name);
                }
            }
        }
    }

    sort(covered.begin(), covered.end());
    for (const auto& robot : covered) {
        cout << robot << endl;
    }

    return 0;
}
