#include <iostream>
#include <queue>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int dx[] = {0, 0, 1, -1};
const int dy[] = {1, -1, 0, 0};

struct Node {
    int x, y, step;
};

int bfs(vector<vector<char>>& map, int x, int y) {
    int n = map.size();
    int m = map[0].size();
    vector<vector<bool>> visited(n, vector<bool>(m, false));
    queue<Node> q;
    q.push({x, y, 0});
    visited[x][y] = true;
    int dirty = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            if (map[i][j] == '*') dirty++;
        }
    }
    while (!q.empty()) {
        Node node = q.front();
        q.pop();
        if (map[node.x][node.y] == '*') dirty--;
        for (int k = 0; k < 4; k++) {
            int nx = node.x + dx[k];
            int ny = node.y + dy[k];
            if (nx >= 0 && nx < n && ny >= 0 && ny < m && !visited[nx][ny] && map[nx][ny] != 'x') {
                q.push({nx, ny, node.step + 1});
                visited[nx][ny] = true;
            }
        }
    }
    return dirty == 0 ? node.step : -1;
}

int main() {
    int n, m;
    while (cin >> n >> m) {
        if (n == 0 && m == 0) break;
        vector<vector<char>> map(n, vector<char>(m));
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                cin >> map[i][j];
            }
        }
        int x, y;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (map[i][j] == 'o') {
                    x = i;
                    y = j;
                }
            }
        }
        cout << bfs(map, x, y) << endl;
    }
    return 0;
}
