#include <stdio.h>
#include <stdlib.h>
#include <math.h>

int gcd(int a, int b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

int is_prime(int num) {
    if (num <= 1)
        return 0;
    for (int i = 2; i <= sqrt(num); i++) {
        if (num % i == 0)
            return 0;
    }
    return 1;
}

int nth_prime(int a, int d, int n) {
    int count = 0;
    int num = a;
    while (1) {
        if (is_prime(num))
            count++;
        if (count == n)
            return num;
        num += d;
    }
}

int main() {
    int a, d, n;
    while (scanf("%d %d %d", &a, &d, &n) == 3) {
        if (a == 0 && d == 0 && n == 0)
            break;
        if (gcd(a, d) != 1) {
            printf("Error: a and d are not relatively prime\n");
            continue;
        }
        printf("%d\n", nth_prime(a, d, n));
    }
    return 0;
}
