#include <stdio.h>
#include <string.h>

#define MAXN 73
#define MAXM 26

int dp[MAXN][MAXN];
int cnt[MAXN][MAXN][MAXM][2];

void init() {
    for (int i = 0; i < MAXN; i++) {
        for (int j = 0; j < MAXN; j++) {
            for (int k = 0; k < MAXM; k++) {
                for (int l = 0; l < 2; l++) {
                    cnt[i][j][k][l] = 0;
                }
            }
        }
    }
    for (int i = 0; i < MAXN; i++) {
        for (int j = 0; j < MAXN; j++) {
            dp[i][j] = 0;
        }
    }
}

void solve(char *s) {
    int n = strlen(s);
    init();
    dp[0][0] = 1;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= i; j++) {
            for (int k = 0; k < MAXM; k++) {
                for (int l = 0; l < 2; l++) {
                    if (dp[i][j] > 0) {
                        cnt[i + 1][j + 1][k][l] += dp[i][j];
                        cnt[i + 1][j][k][l] += dp[i][j];
                    }
                }
            }
        }
        for (int j = 0; j <= n; j++) {
            for (int k = 0; k < MAXM; k++) {
                for (int l = 0; l < 2; l++) {
                    dp[i + 1][j] += dp[i][j];
                    dp[i + 1][j] += cnt[i + 1][j][k][l];
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            for (int k = 0; k < MAXM; k++) {
                for (int l = 0; l < 2; l++) {
                    ans += cnt[n][i][k][l];
                }
            }
        }
    }
    printf("%d\n", ans);
}

int main() {
    int m;
    scanf("%d", &m);
    while (m--) {
        char s[MAXN];
        scanf("%s", s);
        solve(s);
    }
    return 0;
}
