#include <iostream>
#include <string>
#include <unordered_map>

using namespace std;

const int MAXN = 26;
const int MAXM = 72;

unordered_map<string, int> memo;

int dfs(const string& s, int i, int j) {
    if (i >= j) return 1;
    string key = s.substr(i, j - i);
    if (memo.find(key) != memo.end()) return memo[key];
    int res = 0;
    for (int k = i; k < j; ++k) {
        res += dfs(s, i, k) * dfs(s, k + 1, j);
    }
    memo[key] = res;
    return res;
}

int main() {
    int m;
    cin >> m;
    while (m--) {
        string s;
        cin >> s;
        int n = s.size();
        int res = 0;
        for (int i = 1; i < n; ++i) {
            res += dfs(s, 0, i) * dfs(s, i, n);
        }
        cout << res << endl;
    }
    return 0;
}
