#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define MAXN 8
#define MAXK 100
#define MAXD 1000000

int dx[] = {-1, 1, 0, 0, 1, -1, 1, -1};
int dy[] = {0, 0, 1, -1, 1, -1, -1, 1};

int dist(int x1, int y1, int x2, int y2) {
    return abs(x1 - x2) + abs(y1 - y2);
}

int bfs(int n, int x1, int y1, int x2, int y2, int k, int u[], int v[]) {
    int i, j, x, y, d;
    int visited[MAXN][MAXD][MAXD];
    int queue[MAXN][MAXD][MAXD][2];

    for (i = 0; i < n; i++) {
        for (j = 0; j < MAXD; j++) {
            for (int l = 0; l < MAXD; l++) {
                visited[i][j][l] = 0;
            }
        }
    }

    int front = 0, rear = 0;
    queue[0][x1][y1][0] = 0;
    queue[0][x1][y1][1] = 0;
    visited[0][x1][y1] = 1;

    while (front <= rear) {
        x = queue[front][x1][y1][0];
        y = queue[front][x1][y1][1];
        d = queue[front][x1][y1][2];

        if (x == x2 && y == y2) {
            return d;
        }

        for (i = 0; i < 8; i++) {
            int nx = x + dx[i];
            int ny = y + dy[i];

            if (nx >= 0 && nx < n && ny >= 0 && ny < n && !visited[nx][nx][ny]) {
                int flag = 1;
                for (j = 0; j < k; j++) {
                    if (nx == u[j] && ny == v[j]) {
                        flag = 0;
                        break;
                    }
                }

                if (flag) {
                    queue[++rear][nx][ny][0] = nx;
                    queue[rear][nx][ny][1] = ny;
                    queue[rear][nx][ny][2] = d + 1;
                    visited[nx][nx][ny] = 1;
                }
            }
        }

        front++;
    }

    return -1;
}

int main() {
    int n, k, i, j, x, y, u, v, X, Y;
    int x1, y1, x2, y2;
    int d;

    while (scanf("%d", &n) == 1) {
        if (n == 0) {
            break;
        }

        scanf("%d %d", &x1, &y1);
        for (i = 1; i < n; i++) {
            scanf("%d %d", &x, &y);
            x2 = x;
            y2 = y;
        }

        scanf("%d", &k);
        for (i = 0; i < k; i++) {
            scanf("%d %d", &u, &v);
        }

        scanf("%d %d", &X, &Y);

        d = bfs(n, x1, y1, X, Y, k, u, v);
        printf("%d\n", d);
    }

    return 0;
}
