#include <iostream>
#include <vector>
#include <queue>
#include <set>
#include <cmath>

using namespace std;

const int dx[] = {1, -1, 0, 0, 1, -1, -1, 1};
const int dy[] = {0, 0, 1, -1, 1, 1, -1, -1};

struct Node {
    int x, y, step;
    bool operator<(const Node& other) const {
        return step > other.step;
    }
};

int main() {
    int T;
    cin >> T;
    while (T--) {
        int n, k;
        cin >> n;
        vector<pair<int, int>> s(n);
        for (int i = 0; i < n; i++) {
            cin >> s[i].first >> s[i].second;
        }
        cin >> k;
        vector<pair<int, int>> r(k);
        for (int i = 0; i < k; i++) {
            cin >> r[i].first >> r[i].second;
        }
        int X, Y;
        cin >> X >> Y;
        set<pair<int, int>> rocks;
        for (auto& p : r) {
            rocks.insert(p);
        }
        priority_queue<Node> q;
        q.push({s[0].first, s[0].second, 0});
        set<pair<int, int>> vis;
        while (!q.empty()) {
            auto u = q.top();
            q.pop();
            if (u.x == X && u.y == Y) {
                cout << u.step << endl;
                break;
            }
            if (vis.count({u.x, u.y})) continue;
            vis.insert({u.x, u.y});
            for (int i = 0; i < 8; i++) {
                int nx = u.x + dx[i], ny = u.y + dy[i];
                if (rocks.count({nx, ny})) continue;
                for (int j = 0; j < n; j++) {
                    if (abs(nx - s[j].first) + abs(ny - s[j].second) == 1) {
                        if (j == 0 && abs(nx - X) + abs(ny - Y) <= 2) {
                            q.push({nx, ny, u.step + 1});
                        } else if (j != 0 && abs(nx - X) + abs(ny - Y) <= 1) {
                            q.push({nx, ny, u.step + 1});
                        }
                    }
                }
            }
        }
    }
    return 0;
}
