#include <iostream>
#include <queue>
#include <vector>
#include <string>
#include <sstream>

using namespace std;

struct Position {
    int x, y;
};

int main() {
    int w, h;
    while (cin >> w >> h) {
        if (w == 0 && h == 0) break;

        vector<vector<int>> board(h, vector<int>(w));
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                cin >> board[i][j];
            }
        }

        int start_x, start_y;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                if (board[i][j] == 2) {
                    start_x = i;
                    start_y = j;
                }
            }
        }

        queue<Position> q;
        q.push({start_x, start_y});
        vector<vector<bool>> visited(h, vector<bool>(w, false));
        visited[start_x][start_y] = true;
        int moves = 0;
        int goal_x, goal_y;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                if (board[i][j] == 3) {
                    goal_x = i;
                    goal_y = j;
                }
            }
        }

        while (!q.empty()) {
            int size = q.size();
            for (int i = 0; i < size; i++) {
                Position p = q.front();
                q.pop();
                if (p.x == goal_x && p.y == goal_y) {
                    cout << moves << endl;
                    goto next_dataset;
                }

                for (int dx = -1; dx <= 1; dx++) {
                    for (int dy = -1; dy <= 1; dy++) {
                        if (dx == 0 && dy == 0) continue;
                        int nx = p.x + dx;
                        int ny = p.y + dy;
                        if (nx < 0 || nx >= h || ny < 0 || ny >= w) continue;
                        if (visited[nx][ny]) continue;
                        if (board[nx][ny] == 1) {
                            visited[nx][ny] = true;
                            q.push({nx, ny});
                        }
                    }
                }
            }
            moves++;
        }

        cout << -1 << endl;

        next_dataset:;
    }

    return 0;
}
