from collections import deque

def is_valid(x, y, w, h):
    return 0 <= x < w and 0 <= y < h

def bfs(board, w, h):
    start = None
    goal = None
    for i in range(h):
        for j in range(w):
            if board[i][j] == 2:
                start = (i, j)
            elif board[i][j] == 3:
                goal = (i, j)
    if start is None or goal is None:
        return -1
    queue = deque([(start, 0)])
    visited = set([start])
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    while queue:
        (x, y), step = queue.popleft()
        if (x, y) == goal:
            return step
        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            if is_valid(nx, ny, w, h) and board[nx][ny] != 1 and (nx, ny) not in visited:
                queue.append(((nx, ny), step + 1))
                visited.add((nx, ny))
    return -1

def main():
    while True:
        w, h = map(int, input().split())
        if w == 0 and h == 0:
            break
        board = []
        for _ in range(h):
            board.append(list(map(int, input().split())))
        print(bfs(board, w, h))

if __name__ == "__main__":
    main()
