#include <stdio.h>
#include <stdlib.h>
#include <string.h>

typedef struct Node {
    char c;
    int count;
    struct Node* left;
    struct Node* right;
} Node;

Node* createNode(char c) {
    Node* newNode = (Node*)malloc(sizeof(Node));
    newNode->c = c;
    newNode->count = 0;
    newNode->left = NULL;
    newNode->right = NULL;
    return newNode;
}

Node* compress(char* s, int i) {
    Node* root = NULL;
    int j = 0;
    while (s[j] != '(') {
        if (root == NULL) {
            root = createNode(s[j]);
        } else {
            Node* temp = root;
            while (temp->right != NULL) {
                temp = temp->right;
            }
            temp->right = createNode(s[j]);
        }
        j++;
    }
    j++;
    int count = 0;
    while (s[j] >= '0' && s[j] <= '9') {
        count = count * 10 + (s[j] - '0');
        j++;
    }
    Node* temp = root;
    while (temp->right != NULL) {
        temp = temp->right;
    }
    temp->count = count;
    j++;
    if (s[j] == '(') {
        temp->right = compress(s, j);
    }
    return root;
}

void decompress(Node* root, int i, int* result) {
    if (root == NULL) {
        return;
    }
    if (root->count > 0) {
        for (int j = 0; j < root->count; j++) {
            result[i] = root->c;
            i++;
        }
    }
    if (root->right != NULL) {
        decompress(root->right, i, result);
    }
    if (root->left != NULL) {
        decompress(root->left, i, result);
    }
}

int main() {
    char s[101];
    int i;
    while (scanf("%s %d", s, &i) == 2) {
        if (s[0] == '0' && i == 0) {
            break;
        }
        Node* root = compress(s, 0);
        int result[1000001];
        memset(result, 0, sizeof(result));
        decompress(root, 0, result);
        printf("%d\n", result[i]);
    }
    return 0;
}
