#include <iostream>
#include <stack>
#include <string>

using namespace std;

int main() {
    int i;
    string s, temp;
    stack<int> numStack;
    stack<char> charStack;

    while (cin >> s >> i) {
        if (s == "0 0") break;

        int num = 0;
        for (int j = 0; j < s.length(); j++) {
            if (s[j] >= '0' && s[j] <= '9') {
                num = num * 10 + (s[j] - '0');
            } else {
                if (num > 0) {
                    numStack.push(num);
                    charStack.push(s[j]);
                    num = 0;
                } else {
                    charStack.push(s[j]);
                }
            }
        }

        if (num > 0) {
            numStack.push(num);
            charStack.push(s[s.length() - 1]);
        }

        int count = 0;
        while (!charStack.empty()) {
            if (charStack.top() >= 'A' && charStack.top() <= 'Z') {
                count++;
                if (count == i) {
                    cout << charStack.top() << endl;
                    break;
                }
            } else {
                int num = numStack.top();
                numStack.pop();
                for (int j = 0; j < num; j++) {
                    count++;
                    if (count == i) {
                        cout << charStack.top() << endl;
                        break;
                    }
                }
                charStack.pop();
            }
        }
    }

    return 0;
}
