import java.util.Stack;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            String input = scanner.nextLine();
            if (input.equals("0 0")) break;
            String[] parts = input.split(" ");
            String sequence = parts[0];
            int index = Integer.parseInt(parts[1]);
            System.out.println(uncompress(sequence, index));
        }
        scanner.close();
    }

    public static String uncompress(String sequence, int index) {
        Stack<String> stack = new Stack<>();
        StringBuilder sb = new StringBuilder();
        int i = 0;
        while (i < sequence.length()) {
            if (Character.isDigit(sequence.charAt(i))) {
                int j = i;
                while (j < sequence.length() && Character.isDigit(sequence.charAt(j))) j++;
                int count = Integer.parseInt(sequence.substring(i, j));
                i = j;
                if (i < sequence.length() && sequence.charAt(i) == '(') {
                    int k = i + 1;
                    while (k < sequence.length() && sequence.charAt(k) != ')') k++;
                    String subSequence = sequence.substring(i + 1, k);
                    i = k;
                    stack.push(subSequence);
                    sb.append(count);
                } else {
                    sb.append(count);
                }
            } else {
                sb.append(sequence.charAt(i));
                i++;
            }
        }
        String compressed = sb.toString();
        int count = 0;
        for (String s : stack) {
            compressed = compressed.replace(s, "");
            count += compressed.length();
        }
        if (index < count) {
            return String.valueOf(sequence.charAt(index));
        } else {
            return "0";
        }
    }
}
