import math

def find_min_max_angle(points):
    n = len(points)
    points.sort(key=lambda x: x[0])
    min_angle = 0
    max_angle = 0
    min_width = float('inf')
    max_width = 0

    for i in range(n):
        x, y = points[i]
        for j in range(i + 1, n):
            x2, y2 = points[j]
            width = abs(x2 - x)
            angle = math.atan2(y2 - y, x2 - x)
            if angle < 0:
                angle += math.pi
            if angle < min_angle:
                min_angle = angle
                min_width = width
            if angle > max_angle:
                max_angle = angle
                max_width = width

    for i in range(n):
        x, y = points[i]
        for j in range(i + 1, n):
            x2, y2 = points[j]
            width = abs(x2 - x)
            angle = math.atan2(y2 - y, x2 - x)
            if angle < 0:
                angle += math.pi
            if angle > min_angle and width + min_width > max_width:
                max_width = width + min_width
                max_angle = angle
            if angle < max_angle and width + max_width > min_width:
                min_width = width + max_width
                min_angle = angle

    return min_angle, max_angle

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        points = []
        for _ in range(n):
            x, y = map(float, input().split())
            points.append((x, y))
        min_angle, max_angle = find_min_max_angle(points)
        print(f"{min_angle:.10f}")
        print(f"{max_angle:.10f}")

if __name__ == "__main__":
    main()
