#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_JUDGES 100
#define MAX_SCORE 1000

int main() {
    int n, i, j, max, min, sum, count;
    int scores[MAX_JUDGES];

    while (scanf("%d", &n) == 1) {
        if (n == 0) break;

        for (i = 0; i < n; i++) {
            scanf("%d", &scores[i]);
        }

        max = scores[0];
        min = scores[0];
        sum = 0;
        count = 0;

        for (i = 0; i < n; i++) {
            if (scores[i] > max) {
                max = scores[i];
                count = 1;
            } else if (scores[i] == max) {
                count++;
            }

            if (scores[i] < min) {
                min = scores[i];
                count = 1;
            } else if (scores[i] == min) {
                count++;
            }

            if (count > 1) continue;

            sum += scores[i];
            count++;
        }

        printf("%d\n", sum / (count - 2));
    }

    return 0;
}
