#include <iostream>

int main() {
    int n;
    std::cout << "Enter a number: ";
    std::cin >> n;

    if (n < 0 || n > 20) {
        std::cerr << "Error: n should be between 1 and 20." << std::endl;
        return 1;
    }

    long long factorial = 1;
    for (int i = 1; i <= n; ++i) {
        factorial *= i;
    }

    std::cout << "Factorial of " << n << " is: " << factorial << std::endl;

    return 0;
}
