import java.util.*;
import java.io.*;

public class Main {
    static class Record {
        int time;
        int pc;
        int student;
        int type;

        public Record(int time, int pc, int student, int type) {
            this.time = time;
            this.pc = pc;
            this.student = student;
            this.type = type;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        while (true) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            int n = Integer.parseInt(st.nextToken());
            int m = Integer.parseInt(st.nextToken());
            int r = Integer.parseInt(st.nextToken());
            int q = Integer.parseInt(st.nextToken());

            if (n == 0 && m == 0) break;

            List<Record> records = new ArrayList<>();
            for (int i = 0; i < r; i++) {
                st = new StringTokenizer(br.readLine());
                int time = Integer.parseInt(st.nextToken());
                int pc = Integer.parseInt(st.nextToken());
                int student = Integer.parseInt(st.nextToken());
                int type = Integer.parseInt(st.nextToken());
                records.add(new Record(time, pc, student, type));
            }

            for (int i = 0; i < q; i++) {
                st = new StringTokenizer(br.readLine());
                int ts = Integer.parseInt(st.nextToken());
                int te = Integer.parseInt(st.nextToken());
                int student = Integer.parseInt(st.nextToken());
                pw.println(calculateUsage(records, ts, te, student));
            }
        }

        pw.close();
    }

    static int calculateUsage(List<Record> records, int ts, int te, int student) {
        int usage = 0;
        int lastLogout = -1;
        for (Record record : records) {
            if (record.time >= ts && record.time <= te && record.student == student) {
                if (record.type == 1) {
                    if (lastLogout == -1) {
                        usage += record.time - ts;
                    } else {
                        usage += Math.min(record.time, te) - lastLogout;
                    }
                    lastLogout = record.time;
                } else {
                    lastLogout = record.time;
                }
            }
        }
        return usage;
    }
}
