import sys

def read_input():
    lines = sys.stdin.readlines()
    n, m = map(int, lines[0].split())
    records = []
    queries = []
    for i in range(1, len(lines)):
        if lines[i].strip() == '':
            break
        if i == 1:
            r = int(lines[i])
            continue
        if i == 2:
            q = int(lines[i])
            continue
        if i <= r + 2:
            records.append(list(map(int, lines[i].split())))
        else:
            queries.append(list(map(int, lines[i].split())))
    return n, m, records, queries

def process_records(records):
    n, m, records = records
    pc_status = [[] for _ in range(n + 1)]
    for t, n, m, s in records:
        if s == 1:
            pc_status[n].append((m, t))
        else:
            for i, (user, time) in enumerate(pc_status[n]):
                if user == m and time <= t:
                    del pc_status[n][i]
    return pc_status

def process_queries(pc_status, queries):
    for ts, te, m in queries:
        usage = 0
        for pc in range(1, len(pc_status)):
            for i, (user, time) in enumerate(pc_status[pc]):
                if user == m and ts <= time and time < te:
                    usage += te - time
                    del pc_status[pc][i]
        print(usage)

def main():
    n, m, records, queries = read_input()
    pc_status = process_records(records)
    process_queries(pc_status, queries)

if __name__ == '__main__':
    main()
