#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int id;
    int area;
} Piece;

int comparePieces(const void *a, const void *b) {
    Piece *p1 = (Piece *)a;
    Piece *p2 = (Piece *)b;
    return p1->area - p2->area;
}

int main() {
    int n, w, d;
    int i, j, k, l, m, p, s;
    int pieces[100][2];
    int cut[100][2];
    Piece piecesArray[100];
    int count = 0;

    while (scanf("%d %d %d", &n, &w, &d) == 3) {
        if (n == 0 && w == 0 && d == 0) break;

        for (i = 0; i < n; i++) {
            scanf("%d %d", &p, &s);
            cut[i][0] = p;
            cut[i][1] = s;
        }

        for (i = 0; i < n; i++) {
            pieces[i][0] = cut[i][0];
            pieces[i][1] = cut[i][1];
        }

        for (i = 0; i < n; i++) {
            for (j = 0; j < count; j++) {
                if (pieces[i][0] == piecesArray[j].id) {
                    piecesArray[j].area = (piecesArray[j].id - 1) * w * d;
                    break;
                }
            }

            if (pieces[i][0] == piecesArray[count - 1].id) {
                piecesArray[count - 1].area = (piecesArray[count - 1].id - 1) * w * d;
            } else {
                piecesArray[count].id = pieces[i][0];
                piecesArray[count].area = (piecesArray[count].id - 1) * w * d;
                count++;
            }

            if (pieces[i][1] == 1) {
                piecesArray[count - 1].area -= (w - piecesArray[count - 1].id + 1) * d;
                piecesArray[count - 1].id = piecesArray[count - 1].id + (w - piecesArray[count - 1].id + 1);
            } else if (pieces[i][1] == 2) {
                piecesArray[count - 1].area -= (d - piecesArray[count - 1].id + 1) * w;
                piecesArray[count - 1].id = piecesArray[count - 1].id + (d - piecesArray[count - 1].id + 1);
            } else if (pieces[i][1] == 3) {
                piecesArray[count - 1].area -= (w - piecesArray[count - 1].id + 1) * d;
                piecesArray[count - 1].id = piecesArray[count - 1].id + (w - piecesArray[count - 1].id + 1);
            } else if (pieces[i][1] == 4) {
                piecesArray[count - 1].area -= (d - piecesArray[count - 1].id + 1) * w;
                piecesArray[count - 1].id = piecesArray[count - 1].id + (d - piecesArray[count - 1].id + 1);
            }
        }

        qsort(piecesArray, count, sizeof(Piece), comparePieces);

        for (i = 0; i < count; i++) {
            printf("%d ", piecesArray[i].area);
        }

        printf("\n");

        count = 0;
    }

    return 0;
}
