#include <iostream>
#include <vector>
#include <algorithm>

struct Piece {
    int id;
    int area;
};

int main() {
    int n, w, d;
    while (std::cin >> n >> w >> d) {
        if (n == 0 && w == 0 && d == 0) break;

        std::vector<Piece> pieces;
        for (int i = 0; i < n; ++i) {
            int id, start;
            std::cin >> id >> start;
            pieces.push_back({id, (w - start) * (d - 1)});
        }

        std::sort(pieces.begin(), pieces.end(), [](const Piece& a, const Piece& b) {
            return a.area < b.area;
        });

        std::vector<int> areas;
        for (const auto& piece : pieces) {
            if (areas.empty() || piece.area != areas.back()) {
                areas.push_back(piece.area);
            }
        }

        for (const auto& area : areas) {
            for (int i = 0; i < pieces.size(); ++i) {
                if (pieces[i].area == area) {
                    pieces[i].id = areas.size();
                }
            }
        }

        std::sort(pieces.begin(), pieces.end(), [](const Piece& a, const Piece& b) {
            return a.id < b.id;
        });

        for (const auto& piece : pieces) {
            std::cout << piece.area << " ";
        }
        std::cout << std::endl;
    }

    return 0;
}
