class Piece:
    def __init__(self, x, y, w, d):
        self.x = x
        self.y = y
        self.w = w
        self.d = d
        self.cuts = []

    def area(self):
        return self.w * self.d

    def cut(self, x, y):
        self.cuts.append((x, y))
        if self.w > 1:
            self.w -= 1
        else:
            self.w = 1
            self.d -= 1

    def __str__(self):
        return f"{self.x} {self.y} {self.w} {self.d}"


def solve():
    n, w, d = map(int, input().split())
    pieces = [Piece(0, 0, w, d)]
    for _ in range(n):
        p, s = map(int, input().split())
        p -= 1
        piece = pieces[p]
        if s <= piece.w:
            piece.cut(s, 0)
        elif s <= piece.w + piece.d:
            piece.cut(piece.w, s - piece.w)
        else:
            piece.cut(0, s - piece.w - piece.d)
            pieces.append(Piece(piece.x + piece.w, piece.y, piece.w, piece.d))
    areas = sorted([piece.area() for piece in pieces])
    print(" ".join(map(str, areas)))


if __name__ == "__main__":
    while True:
        n, w, d = map(int, input().split())
        if n == 0 and w == 0 and d == 0:
            break
        solve()
```
