#include <iostream>
#include <vector>
#include <queue>
#include <string>
#include <limits>

using namespace std;

const int INF = numeric_limits<int>::max();

struct Position {
    int x, y;
};

struct State {
    int time, left, right;
};

int bfs(vector<vector<char>>& cliff, int w, int h) {
    vector<vector<int>> dist(h, vector<int>(w, INF));
    queue<State> q;

    for (int i = 0; i < h; ++i) {
        for (int j = 0; j < w; ++j) {
            if (cliff[i][j] == 'S') {
                q.push({0, i, j});
                dist[i][j] = 0;
            }
        }
    }

    while (!q.empty()) {
        auto [time, ly, rx] = q.front();
        q.pop();

        for (int dy = -1; dy <= 1; ++dy) {
            for (int dx = -1; dx <= 1; ++dx) {
                int ny = ly + dy;
                int nx = rx + dx;

                if (ny >= 0 && ny < h && nx >= 0 && nx < w) {
                    if (cliff[ny][nx] != 'X') {
                        int ntime = time + (cliff[ny][nx] - '0');
                        int nlx = rx;
                        int nrx = nx;

                        if (ny == ly) {
                            if (nx > rx) {
                                nlx = rx;
                                nrx = nx;
                            } else {
                                nlx = nx;
                                nrx = rx;
                            }
                        }

                        if (dist[ny][nx] > ntime) {
                            dist[ny][nx] = ntime;
                            q.push({ntime, ny, nrx});
                        }
                    }
                }
            }
        }
    }

    int minTime = INF;
    for (int i = 0; i < h; ++i) {
        for (int j = 0; j < w; ++j) {
            if (cliff[i][j] == 'T') {
                minTime = min(minTime, dist[i][j]);
            }
        }
    }

    return minTime == INF ? -1 : minTime;
}

int main() {
    int w, h;
    while (cin >> w >> h) {
        if (w == 0 && h == 0) {
            break;
        }

        vector<vector<char>> cliff(h, vector<char>(w));
        for (int i = 0; i < h; ++i) {
            for (int j = 0; j < w; ++j) {
                cin >> cliff[i][j];
            }
        }

        cout << bfs(cliff, w, h) << endl;
    }

    return 0;
}
