} Point;

double crossProduct(Point p1, Point p2, Point p3) {
    return (p2.x - p1.x) * (p3.y - p1.y) - (p2.y - p1.y) * (p3.x - p1.x);
}

double distance(Point p1, Point p2) {
    return sqrt(pow(p2.x - p1.x, 2) + pow(p2.y - p1.y, 2));
}

double angle(Point p1, Point p2, Point p3) {
    return atan2(crossProduct(p1, p2, p3), distance(p1, p2) * distance(p2, p3));
}

double rotate(double angle, Point p, Point center) {
    double new_x = center.x + (p.x - center.x) * cos(angle) - (p.y - center.y) * sin(angle);
    double new_y = center.y + (p.x - center.x) * sin(angle) + (p.y - center.y) * cos(angle);
