import java.util.*;
import java.io.*;

public class Main {
    static class Point {
        double x, y;

        Point(double x, double y) {
            this.x = x;
            this.y = y;
        }
    }

    static double dist(Point p1, Point p2) {
        return Math.sqrt((p1.x - p2.x) * (p1.x - p2.x) + (p1.y - p2.y) * (p1.y - p2.y));
    }

    static double cross(Point p1, Point p2, Point p3) {
        return (p2.x - p1.x) * (p3.y - p1.y) - (p2.y - p1.y) * (p3.x - p1.x);
    }

    static double dot(Point p1, Point p2) {
        return p1.x * p2.x + p1.y * p2.y;
    }

    static double angle(Point p1, Point p2, Point p3) {
        return Math.acos(dot((p2.x - p1.x, p2.y - p1.y), (p3.x - p1.x, p3.y - p1.y)) / (dist(p1, p2) * dist(p1, p3)));
    }

    static double rotate(double x, double y, double angle) {
        return x * Math.cos(angle) - y * Math.sin(angle);
    }

    static double rotate2(double x, double y, double angle) {
        return x * Math.sin(angle) + y * Math.cos(angle);
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        while (true) {
            double L = sc.nextDouble();
            double R = sc.nextDouble();
            int N = sc.nextInt();
            Point[] points = new Point[N];
            for (int i = 0; i < N; i++) {
                points[i] = new Point(sc.nextDouble(), sc.nextDouble());
            }
            double x = 0, y = L;
            double angle = 0;
            for (int i = 0; i < N; i++) {
                double nextAngle = angle(points[0], points[(i + 1) % N], points[i]);
                if (nextAngle > angle) {
                    angle = nextAngle;
                }
                if (angle > Math.PI) {
                    break;
                }
                x = rotate(x, y, angle);
                y = rotate2(x, y, angle);
                angle += angle(points[0], points[(i + 1) % N], points[i]);
            }
            System.out.printf("%.10f %.10f%n", x, y);
        }
    }
}
