import math

def get_distance(x1, y1, x2, y2):
    return math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

def get_angle(x1, y1, x2, y2):
    return math.atan2(y2 - y1, x2 - x1)

def get_intersection(x1, y1, x2, y2, x3, y3, x4, y4):
    denominator = (x1 - x2) * (y3 - y4) - (y1 - y2) * (x3 - x4)
    if denominator == 0:
        return None
    t = ((x1 - x3) * (y3 - y4) - (y1 - y3) * (x3 - x4)) / denominator
    u = -((x1 - x2) * (y1 - y3) - (y1 - y2) * (x1 - x3)) / denominator
    if 0 <= t <= 1 and 0 <= u <= 1:
        return (x1 + t * (x2 - x1), y1 + t * (y2 - y1))
    return None

def get_point_on_line(x1, y1, x2, y2, distance):
    return (x1 + distance * (x2 - x1) / get_distance(x1, y1, x2, y2),
            y1 + distance * (y2 - y1) / get_distance(x1, y1, x2, y2))

def get_final_position(L, R, N, points):
    for i in range(N):
        x1, y1 = points[i]
        x2, y2 = points[(i + 1) % N]
        if get_distance(x1, y1, x2, y2) > L:
            intersection = get_intersection(x1, y1, x2, y2, 0, 0, x2, y2)
            if intersection:
                x, y = get_point_on_line(x1, y1, x2, y2, L)
                return x, y
    return 0, 0

def main():
    while True:
        L, R, N = map(float, input().split())
        points = []
        for _ in range(N):
            x, y = map(float, input().split())
            points.append((x, y))
        final_x, final_y = get_final_position(L, R, N, points)
        print(f"{final_x:.10f} {final_y:.10f}")
        if L == 0 and R == 0 and N == 0:
            break

if __name__ == "__main__":
    main()
