#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

struct Node {
    string value;
    Node* left;
    Node* right;
};

Node* parseTree(const string& tree) {
    if (tree == "x") {
        return new Node{"x", nullptr, nullptr};
    }

    size_t open = tree.find('(');
    size_t close = tree.find(')');
    string left = tree.substr(open + 1, close - open - 1);
    string right = tree.substr(close + 1, tree.size() - close - 2);

    Node* node = new Node{tree.substr(1, tree.size() - 2), nullptr, nullptr};
    node->left = parseTree(left);
    node->right = parseTree(right);

    return node;
}

void printTree(Node* node) {
    if (node == nullptr) {
        return;
    }

    cout << node->value;
    if (node->left != nullptr || node->right != nullptr) {
        cout << " (";
        printTree(node->left);
        cout << " ";
        printTree(node->right);
        cout << ")";
    }
    cout << endl;
}

void transformTree(Node* node) {
    if (node == nullptr) {
        return;
    }

    if (node->value == "x") {
        return;
    }

    transformTree(node->left);
    transformTree(node->right);

    if (node->left->value == "x" && node->right->value == "x") {
        return;
    }

    if (node->left->value == "x") {
        swap(node->left, node->right);
    }

    if (node->left->value == node->right->value) {
        return;
    }

    if (node->left->value == "x" || node->right->value == "x") {
        return;
    }

    if (node->left->left->value == "x" || node->left->right->value == "x") {
        swap(node->left->left, node->left->right);
    }

    if (node->right->left->value == "x" || node->right->right->value == "x") {
        swap(node->right->left, node->right->right);
    }
}

int main() {
    string line;
    while (getline(cin, line)) {
        if (line == "0") {
            break;
        }

        Node* root = parseTree(line);
        transformTree(root);
        printTree(root);
    }

    return 0;
}
