#include <stdio.h>
#include <stdlib.h>

int main() {
    int n, m, i, j, sum1, sum2, min_sum, min1, min2;
    int *scores1, *scores2;

    while (scanf("%d %d", &n, &m) == 2) {
        if (n == 0 && m == 0) break;

        scores1 = (int *)malloc(n * sizeof(int));
        scores2 = (int *)malloc(m * sizeof(int));

        for (i = 0; i < n; i++) {
            scanf("%d", &scores1[i]);
            sum1 += scores1[i];
        }

        for (i = 0; i < m; i++) {
            scanf("%d", &scores2[i]);
            sum2 += scores2[i];
        }

        if (sum1 == sum2) {
            printf("-1\n");
            free(scores1);
            free(scores2);
            continue;
        }

        min_sum = 10000;
        min1 = -1;
        min2 = -1;

        for (i = 0; i < n; i++) {
            for (j = 0; j < m; j++) {
                int new_sum1 = sum1 - scores1[i] + scores2[j];
                int new_sum2 = sum2 - scores2[j] + scores1[i];

                if (new_sum1 == new_sum2 && new_sum1 < min_sum) {
                    min_sum = new_sum1;
                    min1 = scores1[i];
                    min2 = scores2[j];
                }
            }
        }

        printf("%d %d\n", min1, min2);

        free(scores1);
        free(scores2);
    }

    return 0;
}
