#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    std::vector<int> t, h;
    int n, m, sumT, sumH, minDiff, min1, min2;

    while (true) {
        std::cin >> n >> m;
        if (n == 0 && m == 0) break;

        sumT = 0;
        sumH = 0;
        t.clear();
        h.clear();

        for (int i = 0; i < n; i++) {
            int x;
            std::cin >> x;
            t.push_back(x);
            sumT += x;
        }

        for (int i = 0; i < m; i++) {
            int x;
            std::cin >> x;
            h.push_back(x);
            sumH += x;
        }

        if (sumT == sumH) {
            std::cout << -1 << std::endl;
        } else {
            minDiff = 100000;
            min1 = -1;
            min2 = -1;

            for (int i = 0; i < n; i++) {
                for (int j = 0; j < m; j++) {
                    int tempT = sumT - t[i] + h[j];
                    int tempH = sumH - h[j] + t[i];

                    if (tempT == tempH && tempT < minDiff) {
                        minDiff = tempT;
                        min1 = t[i];
                        min2 = h[j];
                    }
                }
            }

            if (min1 == -1) {
                std::cout << -1 << std::endl;
            } else {
                std::cout << min1 << " " << min2 << std::endl;
            }
        }
    }

    return 0;
}
