#include <stdio.h>
#include <stdlib.h>

// Function to check if a number is a Monday-Saturday prime
int is_monday_saturday_prime(int n) {
    if (n <= 1) return 0;
    for (int i = 2; i * i <= n; i++) {
        if (n % i == 0 && (i % 7 == 1 || i % 7 == 6)) return 0;
        if ((n / i) % 7 == 1 || (n / i) % 7 == 6) return 0;
    }
    return 1;
}

// Function to find Monday-Saturday prime factors
void find_monday_saturday_prime_factors(int n) {
    int i;
    for (i = 2; i * i <= n; i++) {
        if (n % i == 0 && (i % 7 == 1 || i % 7 == 6)) {
            while (n % i == 0) n /= i;
        }
    }
    if (n > 1 && (n % 7 == 1 || n % 7 == 6)) printf("%d ", n);
}

