#include <iostream>
#include <vector>
#include <algorithm>

bool isMondaySaturdayPrime(int n) {
    if (n % 7 != 1 && n % 7 != 6) return false;
    for (int i = 2; i * i <= n; i++) {
        if (n % i == 0 && (i % 7 != 1 && i % 7 != 6)) return false;
    }
    return true;
}

std::vector<int> getMondaySaturdayPrimeFactors(int n) {
    std::vector<int> factors;
    for (int i = 2; i * i <= n; i++) {
        if (n % i == 0 && (i % 7 != 1 && i % 7 != 6)) {
            while (n % i == 0) {
                n /= i;
            }
            factors.push_back(i);
        }
    }
    if (n > 1 && (n % 7 != 1 && n % 7 != 6)) {
        factors.push_back(n);
    }
    return factors;
}

int main() {
    int n;
    while (std::cin >> n) {
        if (n == 1) break;
        std::vector<int> factors = getMondaySaturdayPrimeFactors(n);
        std::sort(factors.begin(), factors.end());
        for (int i = 0; i < factors.size(); i++) {
            std::cout << factors[i];
            if (i < factors.size() - 1) std::cout << " ";
        }
        std::cout << std::endl;
    }
    return 0;
}
