import java.util.*;
import java.io.*;

public class Main {
    static boolean isMondaySaturday(int n) {
        return (n - 1) % 7 == 0 || (n - 6) % 7 == 0;
    }

    static boolean isMondaySaturdayPrime(int n) {
        for (int i = 2; i * i <= n; i++) {
            if (isMondaySaturday(i) && isMondaySaturday(n / i)) {
                return false;
            }
        }
        return true;
    }

    static List<Integer> mondaySaturdayPrimeFactors(int n) {
        List<Integer> factors = new ArrayList<>();
        for (int i = 2; i * i <= n; i++) {
            if (isMondaySaturday(i) && isMondaySaturdayPrime(i) && isMondaySaturdayPrime(n / i)) {
                while (n % i == 0) {
                    factors.add(i);
                    n /= i;
                }
            }
        }
        if (n > 1) {
            factors.add(n);
        }
        return factors;
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line;
        while ((line = br.readLine()) != null) {
            if (line.equals("1")) {
                break;
            }
            int n = Integer.parseInt(line);
            List<Integer> factors = mondaySaturdayPrimeFactors(n);
            Collections.sort(factors);
            System.out.print(n + ": ");
            for (int i = 0; i < factors.size(); i++) {
                System.out.print(factors.get(i));
                if (i < factors.size() - 1) {
                    System.out.print(" ");
                }
            }
            System.out.println();
        }
    }
}
