def is_monday_saturday_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0 and (i % 7 == 1 or i % 7 == 6):
            return False
    return True

def get_monday_saturday_prime_factors(n):
    factors = set()
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0 and (i % 7 == 1 or i % 7 == 6):
            while n % i == 0:
                n //= i
                factors.add(i)
    if n > 1 and (n % 7 == 1 or n % 7 == 6):
        factors.add(n)
    return sorted(list(factors))

def main():
    while True:
        n = int(input())
        if n == 1:
            break
        print(f"{n}: {' '.join(map(str, get_monday_saturday_prime_factors(n)))}")

if __name__ == "__main__":
    main()
