#include <iostream>
#include <string>
#include <vector>
#include <map>
#include <set>
#include <algorithm>

using namespace std;

int eval(const string& formula, map<char, int>& values) {
    if (formula[0] == '(') {
        int pos = formula.find(')');
        string sub = formula.substr(1, pos - 1);
        return eval(sub, values);
    } else if (formula[0] == '-') {
        return 3 - eval(formula.substr(1), values);
    } else if (formula[0] == '*') {
        string sub1 = formula.substr(0, formula.find('*'));
        string sub2 = formula.substr(formula.find('*') + 1);
        return eval(sub1, values) & eval(sub2, values);
    } else if (formula[0] == '+') {
        string sub1 = formula.substr(0, formula.find('+'));
        string sub2 = formula.substr(formula.find('+') + 1);
        return eval(sub1, values) | eval(sub2, values);
    } else {
        return values[formula[0]];
    }
}

int main() {
    map<char, int> values = {{'P', 2}, {'Q', 2}, {'R', 2}};
    string line;
    while (getline(cin, line)) {
        if (line == ".") break;
        int count = 0;
        for (int p = 0; p < 8; p++) {
            for (int q = 0; q < 8; q++) {
                for (int r = 0; r < 8; r++) {
                    values['P'] = p;
                    values['Q'] = q;
                    values['R'] = r;
                    if (eval(line, values) == 2) count++;
                }
            }
        }
        cout << count << endl;
    }
    return 0;
}
