import java.util.HashMap;
import java.util.Map;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Map<String, Integer> cache = new HashMap<>();
        Scanner scanner = new Scanner(System.in);
        while (scanner.hasNextLine()) {
            String line = scanner.nextLine();
            if (line.equals(".")) {
                break;
            }
            System.out.println(evaluate(line, cache));
        }
        scanner.close();
    }

    private static int evaluate(String formula, Map<String, Integer> cache) {
        if (cache.containsKey(formula)) {
            return cache.get(formula);
        }
        if (formula.equals("0") || formula.equals("1") || formula.equals("2")) {
            return formula.equals("2") ? 8 : 0;
        }
        if (formula.charAt(0) == '-') {
            int result = evaluate(formula.substring(1), cache);
            return result == 2 ? 0 : 8 - result;
        }
        int left = evaluate(formula.substring(0, formula.length() - 1), cache);
        int right = evaluate(formula.substring(formula.length() - 1), cache);
        if (formula.charAt(formula.length() - 1) == '*') {
            return left == 2 || right == 2 ? 0 : left == 1 && right == 1 ? 0 : left == 1 && right == 0 ? 1 : left == 0 && right == 1 ? 1 : 7;
        } else {
            return left == 2 || right == 2 ? 8 : left == 1 && right == 1 ? 8 : left == 1 && right == 0 ? 1 : left == 0 && right == 1 ? 1 : 0;
        }
    }
}
