def evaluate_formula(formula):
    def evaluate(x, y, z):
        if x == 0:
            return 0
        elif x == 1:
            return 1
        elif x == 2:
            return 2
        elif x == 'P':
            return z
        elif x == 'Q':
            return y
        elif x == 'R':
            return x

        if x == '-':
            return 3 - evaluate(y, z, z)

        if x == '*':
            return 2 if (evaluate(y, z, z) == 2 and evaluate(y, z, z) == 2) else 0

        if x == '+':
            return 2 if (evaluate(y, z, z) == 2 or evaluate(y, z, z) == 2) else 0

    stack = []
    for char in formula:
        if char in ['*', '+', '-', '(', ')']:
